css:
.print-format{
    padding-left: 0.15in;
    padding-top: 0.1in;
}

.width{
    width: 3.8in;
    max-width: 3.8in;
    height: 3in;
}

p {
  font-size: 18px;
  text-align:center;
}

pre {
    overflow-x: auto;
    white-space: pre-wrap;
    white-space: -moz-pre-wrap;
    white-space: -pre-wrap;
    white-space: -o-pre-wrap;
    word-wrap: normal;
     }

html:
    {%- set desc = [] -%}{%- set appraiser = [] -%}{%- set aappraiser = [] -%}{%- set av = [] -%}{%- for row in doc.non_jewelry_items -%}{%-set nj_item = frappe.get_doc("Non Jewelry Items",row.item_no)-%}{%- if desc.append(row.brand+", "+row.model+", "+row.model_number+", "+nj_item.ram+"/"+nj_item.internal_memory+" IM") -%} {%- endif -%}
    {%- if appraiser.append(nj_item.main_appraiser_name) -%} {%- endif -%}
    {%- if aappraiser.append(nj_item.assistant_appraiser_name) -%} {%- endif -%}
    {%- if av.append(nj_item.appraisal_value) -%} {%- endif -%}
    {%- set acces = [] -%} 
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"charger") == 1 -%} {%- if acces.append("w/ charger") -%}{%- endif -%} {%- else -%} {%- if acces.append("no charger") -%}{%- endif -%}{%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"case") == 1 -%} {%- if acces.append("w/ case") -%}{%- endif -%} {%- else -%} {%- if acces.append("no case") -%}{%- endif -%}{%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"sim_card") == 1 -%} {%- if acces.append("w/ sim") -%}{%- endif -%} {%- else -%} {%- if acces.append("no sim") -%}{%- endif -%}{%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"sd_card") == 1 -%} {%- if acces.append("w/ sd card") -%}{%- endif -%} {%- else -%} {%- if acces.append("no sd card") -%}{%- endif -%}{%- endif -%}
    {%- if desc.append(acces|join(",")) -%}{%- endif -%}
    {%- if (frappe.db.get_value("Non Jewelry Items",row.item_no,"comments") is none) -%}{%- else -%}{%- if desc.append(frappe.db.get_value("Non Jewelry Items",row.item_no,"comments")) -%}{%- endif -%} {%- endif -%}
    {%- set acces = [] -%} 
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"box") == 1 -%} {%- if acces.append("w/ box") -%}{%- endif -%} {%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"earphones") == 1 -%} {%- if acces.append("w/ earphones") -%}{%- endif -%} {%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"pin") == 1 -%} {%- if acces.append("w/ pin") -%}{%- endif -%} {%- endif -%}
    {%- if frappe.db.get_value("Non Jewelry Items",row.item_no,"bag") == 1 -%} {%- if acces.append("w/ bag") -%}{%- endif -%} {%- endif -%}
    {%- if desc.append(acces|join(",")) -%}{%- endif -%}
    {%- if desc.append(",color:"+nj_item.color) -%} {%- endif -%}
    {%- if desc.append("(Pricing: "+nj_item.category+")") -%} {%- endif -%}
    {%- endfor -%}
<pre class="width">
<p>Item# {{ doc.get_formatted("inventory_tracking_no") }}</p>
<B>Date created: {{ doc.date_loan_granted }}</B>
<B>Customer:</B> <U>{{doc.get_formatted("customers_full_name")}}</U>   
<B>Customer Tracker:</B> <U>{{ doc.get_formatted("customers_tracking_no") }}</U>
<B>Item:</B> {{desc|join(" ")}}  
<B>Principal:</B><U>{{ doc.desired_principal }}</U> <B>AV:</B><U>{{av|join("")}}</U>
<span style="font-size:10px;"><B>Main Appraiser: <U>{{appraiser|join("")}}</U>/sign:____________</B></span>
<span style="font-size:10px;"><B>Assisting Appraiser: <U>{{aappraiser|join("")}}</U>/sign:____________</B></span>

</pre>
